tiles.query:
timeseries cpu = max(builtin:containers.cpu.usagePercent), by:{kubernetes.cluster.name}
| filter kubernetes.distribution == "openshift"
| summarize peak_cpu = max(cpu) by:{kubernetes.cluster.name}
| sort peak_cpu desc

timeseries mem = max(builtin:containers.memory.usagePercent), by:{kubernetes.cluster.name}
| filter kubernetes.distribution == "openshift"
| summarize peak_mem = max(mem) by:{kubernetes.cluster.name}
| sort peak_mem desc
